function chkmail()
{
if(document.ordernews.email.value=="" || document.ordernews.email.value=="请输入您的电子邮件地址")
	{
		alert("请输入邮件地址！")
		return false
	}
 else
	 {
	 var email=document.all.email.value
		var pattern=/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(\.[a-zA-Z0-9_-])+/;
		flag=pattern.test(email);
		if (flag)
		{return true;}
		else
			{
			 alert('请输入正确邮箱地址！');
			return false;
			}
	 }
 }

function openwin()
{
            var w=300;
			var h=200;
			var l = (screen.width - w) / 2;
            var t = (screen.height - h) / 2;
            var s = 'width=' + w + ', height=' + h + ', top=' + t + ', left=' + l;
            s += ', toolbar=no, scrollbars=no, menubar=no, location=no, resizable=no';

showwin=window.open("","votewin",s);
showwin.focus();
}
