	document.loginfm.f_password.focus;
	return false
	}

	document.getElementById('loginfm').action="/intranet/h_login.php";
	document.getElementById('loginfm').submit();
}


function chkreg()
{
	var objs = document.getElementsByName("type_login");    
	 for(var i=0;i<objs.length;i++)   
	 {   
	  if(objs[i].checked)   
	  {   
	   type_login=objs[i].value;
	  }   
	}
	//type_login=document.loginfm.type_login.value;
	if(type_login=="teachers")
	{
		document.getElementById('loginfm').action="teacher/teacher_reg.php";
	}
	else if(type_login=="students")
	{
		document.getElementById('loginfm').action="students/students_reg.php";
	}
	document.getElementById('loginfm').submit()
}
-->
</script>

