<script type="text/javascript">

var xmlHttp,xmlHttp1
function userChanged(){
	if (xmlHttp1.readyState==4){
		if(xmlHttp1.status == 200){
			var usertype = xmlHttp1.responseText;
			//usertype=usertype.replace(/\r/ig,"").replace(/\n/ig,"");
			if (usertype!=null||usertype!=""){
				alert(usertype);			
			}else{
				alert("该用户不存在！");
			}
		}
	}
}

function GetXmlHttpObject(){
	var xmlHttp=null;
	try
	  {
	  // Firefox, Opera 8.0+, Safari
	  xmlHttp=new XMLHttpRequest();
	  }
	catch (e)
	  {
	  // Internet Explorer
	  try
	    {
	    xmlHttp=new ActiveXObject("Msxml2.XMLHTTP");
	    }
	  catch (e)
	    {
	    xmlHttp=new ActiveXObject("Microsoft.XMLHTTP");
	    }
	  }
	return xmlHttp;
}
function onSubmitForm(){
        return;
		var userid=document.getElementById("xh").value;
		if (userid==null||userid==""){
			return;
		}	
		xmlHttp1=GetXmlHttpObject();
		if (xmlHttp1==null){
		  alert ("Your browser does not support AJAX!");
		  return;
		}

		xmlHttp1.onreadystatechange=userChanged;
		xmlHttp1.open("GET",url,true);
		xmlHttp1.send(null);
}

function changeDiv1(){	
		document.all("div1").style.display = "";
}


function checkInput()
{
var inputUin=document.form1.uin,
inputPwd=document.form1.pwd;

if(0==inputUin.value.length)
{
inputUin.focus();
return false;
}

if(0==inputPwd.value.length)
{
inputPwd.focus();
return false;
}

document.form1.submit();
setTimeout(" document.form1.pwd.value = '' ",500);
return true;
}

function redirect()
{
    window.location.href="https://exmail.qq.com/cgi-bin/readtemplate?check=false&t=bizmail_orz"; //跳转到b网页
}

