        $(document).ready(function () {
            $(window).load(function () {
                $('#tip1').click(function () {
                    var w_window = ($(window).width()*1 - 220*1) / 2;
                    var h_window = ($('.qr_container').height() * 1 - 220 * 1) / 2;
                    $('.qr_container').css('width', $(window).width());
                    $('#qr_img').css('top', h_window);
                    $('#qr_img').css('left', w_window);
                    $('.qr_container').fadeIn();
                });
                $('#clz').click(function () {
                    $('.qr_container').fadeOut();
                });
            });

        });
    </script>
